import React, { useEffect } from "react";

function CardList({ cards, setCards }) {
  useEffect(() => {
    console.log("Cartas seleccionadas:", cards);
  }, [cards]);

  const handleSelectCard = (card) => {
    // Se elimina la comprobación para permitir duplicados
    setCards([...cards, card]);
    console.log("Carta agregada:", card);
  };

  return (
    <div>
      <h2>Cartas Seleccionadas</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {cards && cards.length > 0 ? (
          cards.map((card, index) => (
            <div
              key={`session-${index}`}
              style={{ margin: "10px", textAlign: "center" }}
            >
              <img
                src={card.card_images[0].image_url}
                alt={card.name}
                width="100"
              />
              <p>{card.name}</p>
            </div>
          ))
        ) : (
          <p>No hay cartas seleccionadas.</p>
        )}
      </div>
      {/* Puedes agregar aquí una sección para mostrar cartas guardadas si lo deseas */}
      {/* Ejemplo: <button onClick={() => handleSelectCard(carta)}>Seleccionar</button> para cada carta guardada */}
    </div>
  );
}

export default CardList;
