import React, { useState } from 'react';

function CardUploader() {
  const [cards, setCards] = useState([]);
  const [error, setError] = useState(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0]; // Obtén el archivo que se sube
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        try {
          const cardData = JSON.parse(reader.result); // Parseamos el JSON
          setCards(cardData); // Actualizamos el estado con los datos de las cartas
          setError(null); // Restablecemos cualquier error previo
        } catch (err) {
          setError('Error al leer el archivo JSON. Asegúrate de que el archivo esté en el formato correcto.');
        }
      };
      reader.readAsText(file); // Leemos el archivo como texto
    }
  };

  return (
    <div>
      <h2>Sube tu colección de cartas</h2>
      
      {/* Campo de carga de archivo */}
      <div>
        <label htmlFor="file-upload" style={{ fontSize: '18px' }}>
          Selecciona tu archivo JSON de cartas:
        </label>
        <input 
          id="file-upload"
          type="file" 
          accept=".json"
          onChange={handleFileUpload}
          style={{ padding: '10px', fontSize: '16px' }}
        />
      </div>
      
      {/* Mensaje de error si ocurre un problema */}
      {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
      
      {/* Mostrar las cartas subidas en una tabla */}
      <div>
        <h3>Colección de cartas:</h3>
        {cards.length > 0 ? (
          <table border="1" cellPadding="10">
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Tipo</th>
                <th>Ataque</th>
                <th>Defensa</th>
                <th>Descripción</th>
                <th>Efecto</th>
              </tr>
            </thead>
            <tbody>
              {cards.map((card, index) => (
                <tr key={index}>
                  <td>{card.name}</td>
                  <td>{card.type}</td>
                  <td>{card.attack}</td>
                  <td>{card.defense}</td>
                  <td>{card.description}</td>
                  <td>{card.effect ? 'Sí' : 'No'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No se han subido cartas aún.</p>
        )}
      </div>
    </div>
  );
}

export default CardUploader;
