import React, { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig";
import { collection, getDocs, query, where } from "firebase/firestore";

function CardList({ cards = [], setCards = () => {} }) {
  const [savedCards, setSavedCards] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Verificar si el usuario est√° autenticado
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      if (user) {
        loadUserDeck(user.uid);
      }
    });

    return () => unsubscribe();
  }, []);

  // üîπ Recuperar las cartas guardadas del usuario
  const loadUserDeck = async (userId) => {
    setLoading(true);
    const decksRef = collection(db, "decks");
    const q = query(decksRef, where("userId", "==", userId));

    try {
      const querySnapshot = await getDocs(q);
      if (!querySnapshot.empty) {
        const deckData = querySnapshot.docs[0].data();
        setSavedCards([...deckData.mainDeck, ...deckData.extraDeck]);
      }
    } catch (error) {
      console.error("Error al cargar el deck:", error);
    }
    setLoading(false);
  };

  // üîπ Permitir seleccionar cartas guardadas para un nuevo deck
  const handleSelectCard = (card) => {
    console.log("Carta seleccionada:", card); // ‚úÖ Verifica si se ejecuta
    if (typeof setCards !== "function") {
      console.error("setCards no est√° definido correctamente.");
      return;
    }
    setCards((prevCards) => [...prevCards, card]); 
  };

  return (
    <div>
      <h2>Cartas Seleccionadas</h2>
      {loading ? <p>Cargando cartas guardadas...</p> : null}

      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {/* Mostrar cartas seleccionadas en la sesi√≥n actual */}
        {cards.map((card, index) => (
          <div key={index} style={{ margin: "10px", textAlign: "center" }}>
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <p>{card.name}</p>
          </div>
        ))}

        {/* Mostrar cartas recuperadas de Firestore con opci√≥n de seleccionarlas */}
        
      </div>
    </div>
  );
}

export default CardList;
