import React, { useState } from "react";

function DeckBuilder({ selectedCards }) {
  const [deckSize, setDeckSize] = useState(20);
  const [mainDeck, setMainDeck] = useState([]);
  const [extraDeck, setExtraDeck] = useState([]);

  // Función para clasificar cartas automáticamente
  const classifyCards = (cards) => {
    let newMainDeck = [...mainDeck];
    let newExtraDeck = [...extraDeck];

    cards.forEach((card) => {
      if (
        card.type.includes("Fusion") ||
        card.type.includes("Synchro") ||
        card.type.includes("XYZ") ||
        card.type.includes("Link")
      ) {
        if (newExtraDeck.length < 15) {
          newExtraDeck.push(card);
        }
      } else {
        if (newMainDeck.length < deckSize) {
          newMainDeck.push(card);
        }
      }
    });

    setMainDeck(newMainDeck);
    setExtraDeck(newExtraDeck);
  };

  // Agregar una carta manualmente al Main Deck
  const addCardToMainDeck = (card) => {
    if (mainDeck.length < deckSize) {
      setMainDeck([...mainDeck, card]);
    } else {
      alert(`El Main Deck ya tiene ${deckSize} cartas.`);
    }
  };

  // Agregar una carta manualmente al Extra Deck
  const addCardToExtraDeck = (card) => {
    if (extraDeck.length < 15) {
      setExtraDeck([...extraDeck, card]);
    } else {
      alert("El Deck Extra ya tiene 15 cartas.");
    }
  };

  // Eliminar una sola copia de una carta del Main Deck
  const removeCardFromMainDeck = (card) => {
    const index = mainDeck.findIndex((c) => c.id === card.id);
    if (index !== -1) {
      const newDeck = [...mainDeck];
      newDeck.splice(index, 1);
      setMainDeck(newDeck);
    }
  };

  // Eliminar una sola copia de una carta del Extra Deck
  const removeCardFromExtraDeck = (card) => {
    const index = extraDeck.findIndex((c) => c.id === card.id);
    if (index !== -1) {
      const newDeck = [...extraDeck];
      newDeck.splice(index, 1);
      setExtraDeck(newDeck);
    }
  };

  return (
    <div>
      <h2>Deck Automático</h2>
      <label>
        Tamaño del Main Deck:
        <input
          type="number"
          min="20"
          max="30"
          value={deckSize}
          onChange={(e) => setDeckSize(Number(e.target.value))}
        />
      </label>
      <button onClick={() => classifyCards(selectedCards)}>Armar Deck</button>

      {/* Sección para agregar cartas manualmente */}
      <h3>Agregar Carta Manualmente</h3>
      <select onChange={(e) => {
        const selectedCard = selectedCards.find(card => card.id === Number(e.target.value));
        if (!selectedCard) return;
        selectedCard.type.includes("Fusion") || selectedCard.type.includes("Synchro") || selectedCard.type.includes("XYZ") || selectedCard.type.includes("Link")
          ? addCardToExtraDeck(selectedCard)
          : addCardToMainDeck(selectedCard);
      }}>
        <option value="">Selecciona una carta</option>
        {selectedCards.map((card) => (
          <option key={card.id} value={card.id}>{card.name}</option>
        ))}
      </select>

      {/* Main Deck */}
      <div>
        <h3>Main Deck ({mainDeck.length}/{deckSize})</h3>
        <ul>
          {mainDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <img src={card.card_images[0].image_url} alt={card.name} width="100" />
              <button onClick={() => removeCardFromMainDeck(card)}>Eliminar</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Deck Extra */}
      <div>
        <h3>Deck Extra ({extraDeck.length}/15)</h3>
        <ul>
          {extraDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <img src={card.card_images[0].image_url} alt={card.name} width="100" />
              <button onClick={() => removeCardFromExtraDeck(card)}>Eliminar</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default DeckBuilder;
