import React, { useState } from "react";

function CardSearch() {
  const [cardName, setCardName] = useState("");
  const [cardData, setCardData] = useState(null);
  const [error, setError] = useState(null);

  const fetchCardData = async () => {
    if (!cardName) return;

    try {
      setError(null);
      setCardData(null);
      const response = await fetch(
        `https://db.ygoprodeck.com/api/v7/cardinfo.php?name=${encodeURIComponent(cardName)}`
      );
      const data = await response.json();
      
      if (data.data) {
        setCardData(data.data[0]);
      } else {
        setError("Carta no encontrada");
      }
    } catch (err) {
      setError("Error al obtener los datos");
    }
  };

  return (
    <div>
      <h2>Buscar Carta de Yu-Gi-Oh!</h2>
      <input
        type="text"
        placeholder="Nombre de la carta"
        value={cardName}
        onChange={(e) => setCardName(e.target.value)}
      />
      <button onClick={fetchCardData}>Buscar</button>
      
      {error && <p style={{ color: "red" }}>{error}</p>}
      {cardData && (
        <div>
          <h3>{cardData.name}</h3>
          <img src={cardData.card_images[0].image_url} alt={cardData.name} width="200" />
          <p>{cardData.type}</p>
          <p>{cardData.desc}</p>
        </div>
      )}
    </div>
  );
}

export default CardSearch;
