import React, { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig";
import { collection, getDocs, query, where } from "firebase/firestore";

function CardList({ cards, setCards }) {
  const [savedCards, setSavedCards] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      if (user) {
        console.log("Usuario autenticado:", user.uid);
        loadUserDeck(user.uid);
      }
    });
    return () => unsubscribe();
  }, []);

  const loadUserDeck = async (userId) => {
    setLoading(true);
    const decksRef = collection(db, "decks");
    const q = query(decksRef, where("userId", "==", userId));

    try {
      const querySnapshot = await getDocs(q);
      if (!querySnapshot.empty) {
        const deckData = querySnapshot.docs[0].data();
        console.log("Datos brutos de Firestore:", deckData);

        const loadedCards = [...deckData.mainDeck, ...deckData.extraDeck].map((card) => ({
          name: card.name || "Carta Desconocida",
          type: card.type || "Desconocido",
          card_images: card.card_images || [{ image_url: "https://via.placeholder.com/100" }],
        }));

        console.log("Cartas después de formatear:", loadedCards);
        setSavedCards(loadedCards);
      }
    } catch (error) {
      console.error("Error al cargar el deck:", error);
    }
    setLoading(false);
  };

  const handleSelectCard = (selectedCard) => {
    if (!cards.some((c) => c.name === selectedCard.name)) {
      setCards([...cards, selectedCard]);
    }
  };

  return (
    <div>
      <h2>Cartas Seleccionadas</h2>

      {loading ? <p>Cargando cartas guardadas...</p> : null}

      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {cards.map((card, index) => (
          <div key={index} style={{ margin: "10px", textAlign: "center", cursor: "pointer" }}
               onClick={() => handleSelectCard(card)}>
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <p>{card.name}</p>
          </div>
        ))}

        {savedCards.map((card, index) => (
          <div key={`saved-${index}`} style={{ margin: "10px", textAlign: "center", cursor: "pointer" }}
               onClick={() => handleSelectCard(card)}>
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <p>{card.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CardList;
