import React, { useState } from "react";

function DeckBuilder({ addToDeck }) {
  const [archetype, setArchetype] = useState("");
  const [deck, setDeck] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchDeck = async () => {
    if (!archetype) {
      setError("Por favor, ingresa un arquetipo.");
      return;
    }
    setLoading(true);
    setError("");
    setDeck([]);

    try {
      const response = await fetch(
        `https://db.ygoprodeck.com/api/v7/cardinfo.php?archetype=${encodeURIComponent(archetype)}`
      );
      const data = await response.json();

      if (data.data) {
        const selectedDeck = data.data.slice(0, 20); // Seleccionamos hasta 20 cartas
        setDeck(selectedDeck);
      } else {
        setError("No se encontraron cartas para este arquetipo.");
      }
    } catch (err) {
      setError("Error al buscar el deck.");
    }

    setLoading(false);
  };

  return (
    <div>
      <h2>Generador Automático de Deck</h2>
      <input
        type="text"
        value={archetype}
        onChange={(e) => setArchetype(e.target.value)}
        placeholder="Escribe el arquetipo del deck..."
      />
      <button onClick={fetchDeck}>Generar Deck</button>

      {loading && <p>Generando deck...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <div>
        {deck.length > 0 && <h3>Deck Generado:</h3>}
        {deck.map((card) => (
          <div key={card.id}>
            <h4>{card.name}</h4>
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <p><strong>Tipo:</strong> {card.type}</p>
            <p><strong>Descripción:</strong> {card.desc}</p>
            <button onClick={() => addToDeck(card)}>Agregar al mazo</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DeckBuilder;
