import React, { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig"; // Importa Firestore y Auth
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import CardList from "./CardList";

function DeckBuilder({ selectedCards }) {
  const [deckSize, setDeckSize] = useState(20);
  const [mainDeck, setMainDeck] = useState([]);
  const [extraDeck, setExtraDeck] = useState([]);
  const [selectedCardId, setSelectedCardId] = useState("");
  const [keyCards, setKeyCards] = useState([]);
  const [user, setUser] = useState(null);
  const [loadingDecks, setLoadingDecks] = useState(true);
  const [cards, setCards] = useState(selectedCards || []); // ✅ Se inicializa correctamente

  useEffect(() => {
    console.log("Cartas seleccionadas actualizadas:", cards);
  }, [cards]);

  // Cargar decks guardados del usuario
  

  // Guardar deck en Firestore
  const saveDeckToFirestore = async () => {
    if (!user) {
      alert("Debes iniciar sesión para guardar el deck.");
      return;
    }

    const deckRef = collection(db, "decks");
    try {
      await addDoc(deckRef, {
        userId: user.uid,
        mainDeck,
        extraDeck,
        timestamp: new Date(),
      });
      alert("Deck guardado con éxito!");
    } catch (error) {
      console.error("Error guardando deck:", error);
      alert("Hubo un error al guardar el deck.");
    }
  };

  // Clasificar cartas automáticamente
  const classifyCards = (selectedCards) => {
    let newMainDeck = [];
    let newExtraDeck = [];

    selectedCards.forEach((card) => {
      if (
        card.type.includes("Fusion") ||
        card.type.includes("Synchro") ||
        card.type.includes("XYZ") ||
        card.type.includes("Link")
      ) {
        if (newExtraDeck.length < 15) {
          newExtraDeck.push(card);
        }
      } else {
        if (newMainDeck.length < deckSize) {
          newMainDeck.push(card);
        }
      }
    });

    setMainDeck(newMainDeck);
    setExtraDeck(newExtraDeck);
  };

  return (
    <div>
      <h2>Deck Automático</h2>

      {/* ✅ Se mantiene setCards en CardList sin duplicar cartas */}
      <CardList cards={cards} setCards={setCards} />

      <label>
        Tamaño del Main Deck:
        <input
          type="number"
          min="20"
          max="30"
          value={deckSize}
          onChange={(e) => setDeckSize(Number(e.target.value))}
        />
      </label>
      <button onClick={() => classifyCards(cards)}>Armar Deck</button>
      <button onClick={saveDeckToFirestore}>Guardar Deck</button>

      {loadingDecks && <p>Cargando decks guardados...</p>}

      {/* Main Deck */}
      <div>
        <h3>Main Deck ({mainDeck.length}/{deckSize})</h3>
        <ul>
          {mainDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <button onClick={() => setMainDeck(mainDeck.filter((c, i) => i !== index))}>
                Eliminar
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Extra Deck */}
      <div>
        <h3>Extra Deck ({extraDeck.length}/15)</h3>
        <ul>
          {extraDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <button onClick={() => setExtraDeck(extraDeck.filter((c, i) => i !== index))}>
                Eliminar
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default DeckBuilder;
