import React from "react";

function DeckBuilder({ selectedCards }) {
  return (
    <div>
      <h2>Tu Mazo</h2>
      {selectedCards.length > 0 ? (
        <ul>
          {selectedCards.map((card, index) => (
            <li key={index}>{card.name}</li>
          ))}
        </ul>
      ) : (
        <p>No hay cartas en tu mazo.</p>
      )}
    </div>
  );
}

export default DeckBuilder;
