import React, { useState } from 'react';

function CardUploader() {
  const [cardData, setCardData] = useState({
    name: '',
    type: '',
    description: '',
    isEffect: false,
  });

  const [cards, setCards] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCardData({
      ...cardData,
      [name]: value,
    });
  };

  const handleCheckboxChange = (e) => {
    setCardData({
      ...cardData,
      isEffect: e.target.checked,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Al enviar el formulario, agrega la carta al estado de cartas
    setCards([...cards, cardData]);

    // Resetea los campos del formulario
    setCardData({
      name: '',
      type: '',
      description: '',
      isEffect: false,
    });
  };

  return (
    <div>
      <h2>Subir Carta</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            name="name"
            value={cardData.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Tipo:</label>
          <input
            type="text"
            name="type"
            value={cardData.type}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Descripción:</label>
          <textarea
            name="description"
            value={cardData.description}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>¿Es de efecto?</label>
          <input
            type="checkbox"
            checked={cardData.isEffect}
            onChange={handleCheckboxChange}
          />
        </div>
        <button type="submit">Agregar Carta</button>
      </form>

      <h3>Carta(s) Cargadas:</h3>
      <ul>
        {cards.map((card, index) => (
          <li key={index}>
            <h4>{card.name}</h4>
            <p>Tipo: {card.type}</p>
            <p>Descripción: {card.description}</p>
            <p>{card.isEffect ? 'Es una carta de efecto' : 'No es una carta de efecto'}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CardUploader;
