import React, { useState, useEffect } from "react";

function DeckBuilder({ selectedCards }) {
  // Eliminamos las importaciones y variables de Firebase
  const [deckSize, setDeckSize] = useState(20);
  const [mainDeck, setMainDeck] = useState([]);
  const [extraDeck, setExtraDeck] = useState([]);
  const [selectedCardId, setSelectedCardId] = useState("");
  const [keyCards, setKeyCards] = useState([]);
  const [loadingDecks, setLoadingDecks] = useState(false); // Ya no cargamos datos desde DB

  // Función para unificar el formato de una carta (si se necesita)
  const standardizeCard = (card) => {
    return {
      id: Number(card.card_images?.[0]?.id || card.id),
      name: card.name,
      type: card.type,
      card_images: card.card_images || [{ image_url: "https://via.placeholder.com/100", id: 0 }],
    };
  };

  // Como ya no usamos Firestore, quitamos las funciones loadUserDecks y saveDeckToFirestore.
  // En su lugar, puedes implementar persistencia local (por ejemplo, con localStorage)
  // Si no quieres persistencia, simplemente elimina estas funciones.

  // Función para clasificar cartas automáticamente
  const classifyCards = (cards) => {
    let newMainDeck = [];
    let newExtraDeck = [];
    cards.forEach((card) => {
      const stdCard = standardizeCard(card);
      if (["Fusion", "Synchro", "XYZ", "Link"].some((type) => stdCard.type.includes(type))) {
        if (newExtraDeck.length < 15) newExtraDeck.push(stdCard);
      } else {
        if (newMainDeck.length < deckSize) newMainDeck.push(stdCard);
      }
    });
    setMainDeck(newMainDeck);
    setExtraDeck(newExtraDeck);
  };

  // Construir deck alrededor de cartas clave
  const buildAroundKeyCards = () => {
    let filteredCards = selectedCards.filter((card) =>
      keyCards.some(
        (keyCard) =>
          Number(standardizeCard(keyCard).id) === Number(standardizeCard(card).id)
      )
    );
    classifyCards(filteredCards);
  };

  // Agregar carta clave
  const addKeyCard = (card) => {
    const stdCard = standardizeCard(card);
    if (!keyCards.some((keyCard) => Number(standardizeCard(keyCard).id) === Number(stdCard.id))) {
      setKeyCards([...keyCards, stdCard]);
    }
  };

  // Agregar carta manualmente
  const addSelectedCard = () => {
    const selectedCard = selectedCards.find(
      (card) => Number(standardizeCard(card).id) === Number(selectedCardId)
    );
    if (!selectedCard) return;
    if (
      ["Fusion", "Synchro", "XYZ", "Link"].some((type) =>
        selectedCard.type.includes(type)
      )
    ) {
      addCardToExtraDeck(selectedCard);
    } else {
      addCardToMainDeck(selectedCard);
    }
  };

  const addCardToMainDeck = (card) => {
    const stdCard = standardizeCard(card);
    if (mainDeck.length < deckSize) setMainDeck([...mainDeck, stdCard]);
    else alert(`El Main Deck ya tiene ${deckSize} cartas.`);
  };

  const addCardToExtraDeck = (card) => {
    const stdCard = standardizeCard(card);
    if (extraDeck.length < 15) setExtraDeck([...extraDeck, stdCard]);
    else alert("El Deck Extra ya tiene 15 cartas.");
  };

  const removeCardFromMainDeck = (card) => {
    const stdCard = standardizeCard(card);
    setMainDeck(mainDeck.filter((c) => Number(c.id) !== Number(stdCard.id)));
  };

  const removeCardFromExtraDeck = (card) => {
    const stdCard = standardizeCard(card);
    setExtraDeck(extraDeck.filter((c) => Number(c.id) !== Number(stdCard.id)));
  };

  return (
    <div>
      <h2>Deck Automático</h2>
      <label>
        Tamaño del Main Deck:
        <input
          type="number"
          min="20"
          max="30"
          value={deckSize}
          onChange={(e) => setDeckSize(Number(e.target.value))}
        />
      </label>
      <button onClick={() => classifyCards(selectedCards)}>Armar Deck</button>
      {/* Aquí podrías agregar persistencia local si deseas guardar el deck sin DB */}
      <button onClick={() => console.log("Deck actual:", { mainDeck, extraDeck })}>Ver Deck en Consola</button>

      <h3>Seleccionar Cartas Clave</h3>
      <select onChange={(e) => addKeyCard(selectedCards.find((c) => Number(c.id) === Number(e.target.value)))} defaultValue="">
        <option value="">Selecciona una carta</option>
        {selectedCards.map((card) => (
          <option key={card.id} value={card.id}>{card.name}</option>
        ))}
      </select>
      <button onClick={buildAroundKeyCards}>Construir alrededor de cartas clave</button>

      <h3>Agregar Carta Manualmente</h3>
      <select onChange={(e) => setSelectedCardId(e.target.value)} defaultValue="">
        <option value="">Selecciona una carta</option>
        {selectedCards.map((card) => (
          <option key={card.id} value={card.id}>{card.name}</option>
        ))}
      </select>
      <button onClick={addSelectedCard}>Añadir Carta</button>

      <div>
        <h3>Main Deck ({mainDeck.length}/{deckSize})</h3>
        <ul>
          {mainDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <button onClick={() => removeCardFromMainDeck(card)}>Eliminar</button>
            </li>
          ))}  
        </ul>
      </div>

      <div>
        <h3>Extra Deck ({extraDeck.length}/15)</h3>
        <ul>
          {extraDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <button onClick={() => removeCardFromExtraDeck(card)}>Eliminar</button>
            </li>
          ))}  
        </ul>
      </div>
    </div>
  );
}

export default DeckBuilder;
