    apiKey: "AIzaSyB6dvnnBSRHIcNGDUSddWtiUZYxBaE_lp4",
    authDomain: "deckbuilder-be4c1.firebaseapp.com",
    projectId: "deckbuilder-be4c1",
    storageBucket: "deckbuilder-be4c1.appspot.com", // <- Corrección en storageBucket
    messagingSenderId: "560470608370",
    appId: "1:560470608370:web:4af83cbd57ab034b6f836b",
    measurementId: "G-6NDQCHGN94"
};

// Inicializar Firebase
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
