import React, { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig"; 
import { collection, getDocs, doc, setDoc } from "firebase/firestore";

function CardList({ cards }) {
  const [savedCards, setSavedCards] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      if (user) {
        console.log("Usuario autenticado:", user.uid);
        loadUserDeck(user.uid);
      } else {
        console.warn("No hay usuario autenticado.");
      }
    });
    return () => unsubscribe();
  }, []);
  

  // üîπ Recuperar las cartas guardadas del usuario en Firestore
  const loadUserDeck = async (userId) => {
    setLoading(true);
    const decksRef = collection(db, "users", userId, "decks");

    try {
      const querySnapshot = await getDocs(decksRef);
      if (!querySnapshot.empty) {
        const latestDeck = querySnapshot.docs[0].data(); 
        console.log("Cartas recuperadas de Firestore:", latestDeck.cards);
        setSavedCards(latestDeck.cards || []);
      } else {
        console.log("No hay decks guardados para este usuario.");
        setSavedCards([]); 
      }
    } catch (error) {
      console.error("Error al cargar el deck:", error);
    }
    setLoading(false);
  };

  // üîπ Guardar el deck actual en Firestore
  const saveDeck = async (deckName) => {
    if (!user) {
      console.error("Usuario no autenticado.");
      return;
    }

    try {
      const deckRef = doc(db, "users", user.uid, "decks", deckName);
      await setDoc(deckRef, {
        name: deckName,
        cards: cards, 
        timestamp: new Date(),
      });
      console.log("Deck guardado correctamente!");
    } catch (error) {
      console.error("Error al guardar el deck:", error);
    }
  };

  return (
    <div>
      <h2>Cartas Seleccionadas</h2>

      {loading ? <p>Cargando cartas guardadas...</p> : null}

      <button onClick={() => saveDeck("MiDeck")}>Guardar Deck</button>

      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {/* Mostrar cartas seleccionadas en la sesi√≥n actual */}
        {cards.length > 0 && <h3>Cartas en la sesi√≥n</h3>}
        {cards.map((card, index) => (
          <div key={index} style={{ margin: "10px", textAlign: "center" }}>
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <p>{card.name}</p>
          </div>
        ))}

        {/* Mostrar cartas recuperadas de Firestore */}
        {savedCards.length > 0 && <h3>Cartas guardadas</h3>}
        {savedCards.map((card, index) => (
          <div key={`saved-${index}`} style={{ margin: "10px", textAlign: "center" }}>
            <img src={card.card_images[0]?.image_url} alt={card.name} width="100" />
            <p>{card.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CardList;
