import React, { useState } from "react";
import CardSearch from "./components/CardSearch";
import CardList from "./components/CardList";
import DeckBuilder from "./components/DeckBuilder";

function App() {
  // Estado para las cartas seleccionadas en la sesión
  const [selectedCards, setSelectedCards] = useState([]);

  const addToDeck = (card) => {
    const cardCount = selectedCards.filter((c) => c.id === card.id).length;

    if (cardCount < 3) {
      setSelectedCards([...selectedCards, { ...card, uniqueId: Date.now() }]);
    } else {
      alert("Solo puedes agregar hasta 3 copias de la misma carta.");
    }
  };

  return (
    <div>
      <h1>Speed Duel Deck Builder</h1>
      {/* CardSearch es el componente que te permite buscar cartas (sin DB) */}
      <CardSearch addToDeck={addToDeck} />
      {/* Se muestra la lista de cartas seleccionadas y se permite modificarlas */}
      <CardList cards={selectedCards} setCards={setSelectedCards} />
      {/* DeckBuilder usa las cartas seleccionadas para armar el deck */}      
      <DeckBuilder selectedCards={selectedCards} />
    </div>
  );
}

export default App;
