import React, { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig"; 
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";

function DeckBuilder({ selectedCards }) {
  const [deckSize, setDeckSize] = useState(20);
  const [mainDeck, setMainDeck] = useState([]);
  const [extraDeck, setExtraDeck] = useState([]);
  const [selectedCardId, setSelectedCardId] = useState("");
  const [keyCards, setKeyCards] = useState([]);
  const [user, setUser] = useState(null);
  const [loadingDecks, setLoadingDecks] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      if (user) {
        loadUserDecks(user.uid);
      }
    });
    return () => unsubscribe();
  }, []);

  const loadUserDecks = async (userId) => {
    setLoadingDecks(true);
    const decksRef = collection(db, "decks");
    const q = query(decksRef, where("userId", "==", userId));
  
    try {
      const querySnapshot = await getDocs(q);
      if (!querySnapshot.empty) {
        querySnapshot.forEach((doc) => {
          const deckData = doc.data();
          console.log("Deck cargado desde Firestore:", deckData);
  
          // Asegurar que el formato sea el correcto
          const formattedMainDeck = deckData.mainDeck?.map((card) => ({
            id: card.card_images?.[0]?.id || card.id, // Asegurar ID correcto
            name: card.name,
            type: card.type,
            image: card.card_images?.[0]?.image_url, // Asegurar imagen
          })) || [];
  
          const formattedExtraDeck = deckData.extraDeck?.map((card) => ({
            id: card.card_images?.[0]?.id || card.id,
            name: card.name,
            type: card.type,
            image: card.card_images?.[0]?.image_url,
          })) || [];
  
          setMainDeck(formattedMainDeck);
          setExtraDeck(formattedExtraDeck);
        });
      } else {
        console.log("No se encontraron decks guardados.");
      }
    } catch (error) {
      console.error("Error cargando decks:", error);
    }
    setLoadingDecks(false);
  };
  

  const saveDeckToFirestore = async () => {
    if (!user) {
      alert("Debes iniciar sesión para guardar el deck.");
      return;
    }
    try {
      await addDoc(collection(db, "decks"), {
        userId: user.uid,
        mainDeck,
        extraDeck,
        timestamp: new Date(),
      });
      alert("Deck guardado con éxito!");
    } catch (error) {
      console.error("Error guardando deck:", error);
      alert("Hubo un error al guardar el deck.");
    }
  };

  const classifyCards = (cards) => {
    let newMainDeck = [...mainDeck];
    let newExtraDeck = [...extraDeck];
    cards.forEach((card) => {
      if (["Fusion", "Synchro", "XYZ", "Link"].some(type => card.type.includes(type))) {
        if (newExtraDeck.length < 15) newExtraDeck.push(card);
      } else {
        if (newMainDeck.length < deckSize) newMainDeck.push(card);
      }
    });
    setMainDeck(newMainDeck);
    setExtraDeck(newExtraDeck);
  };

  const buildAroundKeyCards = () => {
    let filteredCards = selectedCards.filter((card) =>
      keyCards.some((keyCard) => keyCard.card_images[0].id === card.card_images[0].id)
    );
  
    classifyCards(filteredCards);
  };
  

  const addKeyCard = (card) => {
    if (!keyCards.some((keyCard) => keyCard.card_images[0].id === card.card_images[0].id)) {
      setKeyCards([...keyCards, card]);
    }
  };
  

  const addSelectedCard = () => {
    setSelectedCards((prevCards) => [...prevCards, card]); // Asegura que React detecte el cambio
    const selectedCard = selectedCards.find(
      (card) => card.card_images[0].id === Number(selectedCardId)
    );
  
    if (!selectedCard) return;
  
    if (
      selectedCard.type.includes("Fusion") ||
      selectedCard.type.includes("Synchro") ||
      selectedCard.type.includes("XYZ") ||
      selectedCard.type.includes("Link")
    ) {
      addCardToExtraDeck(selectedCard);
    } else {
      addCardToMainDeck(selectedCard);
    }
  };
  

  const addCardToMainDeck = (card) => {
    if (mainDeck.length < deckSize) setMainDeck([...mainDeck, card]);
    else alert(`El Main Deck ya tiene ${deckSize} cartas.`);
  };

  const addCardToExtraDeck = (card) => {
    if (extraDeck.length < 15) setExtraDeck([...extraDeck, card]);
    else alert("El Deck Extra ya tiene 15 cartas.");
  };

  const removeCardFromMainDeck = (card) => {
    const index = mainDeck.findIndex((c) => c.card_images[0].id === card.card_images[0].id);
    if (index !== -1) {
      const newDeck = [...mainDeck];
      newDeck.splice(index, 1);
      setMainDeck(newDeck);
    }
  };
  

  const removeCardFromExtraDeck = (card) => {
    setExtraDeck(extraDeck.filter((c) => c.id !== card.id));
  };

  return (
    <div>
      <h2>Deck Automático</h2>
      <label>
        Tamaño del Main Deck:
        <input
          type="number"
          min="20"
          max="30"
          value={deckSize}
          onChange={(e) => setDeckSize(Number(e.target.value))}
        />
      </label>
      <button onClick={() => classifyCards(selectedCards)}>Armar Deck</button>
      <button onClick={saveDeckToFirestore}>Guardar Deck</button>
      {loadingDecks && <p>Cargando decks guardados...</p>}
      <h3>Seleccionar Cartas Clave</h3>
      <select onChange={(e) => addKeyCard(selectedCards.find((c) => c.id === Number(e.target.value)))}>
        <option value="">Selecciona una carta</option>
        {selectedCards.map((card) => (
          <option key={card.id} value={card.id}>{card.name}</option>
        ))}
      </select>
      <button onClick={buildAroundKeyCards}>Construir alrededor de cartas clave</button>
      <h3>Agregar Carta Manualmente</h3>
      <select onChange={(e) => setSelectedCardId(e.target.value)}>
        <option value="">Selecciona una carta</option>
        {selectedCards.map((card) => (
          <option key={card.id} value={card.id}>{card.name}</option>
        ))}
      </select>
      <button onClick={addSelectedCard}>Añadir Carta</button>
      <div>
        <h3>Main Deck ({mainDeck.length}/{deckSize})</h3>
        <ul>
          {mainDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <button onClick={() => removeCardFromMainDeck(card)}>Eliminar</button>
            </li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Extra Deck ({extraDeck.length}/15)</h3>
        <ul>
          {extraDeck.map((card, index) => (
            <li key={`${card.id}-${index}`}>
              <strong>{card.name}</strong> - {card.type}
              <button onClick={() => removeCardFromExtraDeck(card)}>Eliminar</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default DeckBuilder;
