import React, { useState } from "react";

function DeckBuilder({ selectedCards }) {
  const [deckSize, setDeckSize] = useState(20);
  const [finalDeck, setFinalDeck] = useState([]);
  const [extraDeck, setExtraDeck] = useState([]);

  const buildDeck = () => {
    if (selectedCards.length < deckSize) {
      alert("No tienes suficientes cartas seleccionadas para armar el deck.");
      return;
    }

    const cardGroups = selectedCards.reduce((acc, card) => {
      acc[card.type] = acc[card.type] || [];
      acc[card.type].push(card);
      return acc;
    }, {});

    const sortedCards = Object.values(cardGroups)
      .flat()
      .sort((a, b) => b.atk - a.atk);

    setFinalDeck(sortedCards.slice(0, deckSize));
  };

  const addToExtraDeck = (card) => {
    if (extraDeck.length >= 15) {
      alert("El Deck Extra ya tiene 15 cartas.");
      return;
    }
    if (["Fusion Monster", "Synchro Monster", "XYZ Monster", "Link Monster"].includes(card.type)) {
      setExtraDeck([...extraDeck, card]);
    } else {
      alert("Solo puedes agregar cartas de Fusión, Sincronía, XYZ o Enlace al Deck Extra.");
    }
  };

  const removeFromDeck = (card) => {
    setFinalDeck(finalDeck.filter((c) => c.id !== card.id));
  };

  const removeFromExtraDeck = (card) => {
    setExtraDeck(extraDeck.filter((c) => c.id !== card.id));
  };

  return (
    <div>
      <h2>Deck Automático</h2>
      <label>
        Tamaño del Deck:
        <input
          type="number"
          min="20"
          max="30"
          value={deckSize}
          onChange={(e) => setDeckSize(Number(e.target.value))}
        />
      </label>
      <button onClick={buildDeck}>Armar Deck</button>

      <h3>Main Deck ({finalDeck.length}/{deckSize})</h3>
      <ul>
        {finalDeck.map((card, index) => (
          <li key={index}>
            <strong>{card.name}</strong> - {card.type}
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <button onClick={() => removeFromDeck(card)}>Eliminar</button>
          </li>
        ))}
      </ul>

      <h3>Deck Extra ({extraDeck.length}/15)</h3>
      <ul>
        {extraDeck.map((card, index) => (
          <li key={index}>
            <strong>{card.name}</strong> - {card.type}
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <button onClick={() => removeFromExtraDeck(card)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DeckBuilder;
