import React, { useEffect } from "react";

function CardList({ cards, setCards }) {
  // Guardar la lista de cartas en localStorage cuando cambie
  useEffect(() => {
    localStorage.setItem("selectedCards", JSON.stringify(cards));
  }, [cards]);

  // Función para eliminar una carta de la lista
  const removeCard = (index) => {
    const newCards = [...cards];
    newCards.splice(index, 1);
    setCards(newCards);
  };

  return (
    <div>
      <h2>Cartas Seleccionadas</h2>
      <ul>
        {cards.map((card, index) => (
          <li key={`${card.id}-${index}`}>
            <strong>{card.name}</strong> - {card.type}
            <button onClick={() => removeCard(index)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CardList;
