import React, { useState } from "react";

function AutoDeckBuilder() {
  const [archetype, setArchetype] = useState("");
  const [deckSize, setDeckSize] = useState(40); // Tamaño por defecto
  const [deck, setDeck] = useState([]);

  const fetchArchetypeCards = async () => {
    if (!archetype) return;

    try {
      const response = await fetch(
        `https://db.ygoprodeck.com/api/v7/cardinfo.php?archetype=${encodeURIComponent(archetype)}`
      );
      const data = await response.json();

      if (data.data) {
        const shuffledCards = data.data.sort(() => 0.5 - Math.random()); // Barajamos las cartas
        const selectedDeck = shuffledCards.slice(0, deckSize); // Seleccionamos el número deseado
        setDeck(selectedDeck);
      } else {
        setDeck([]);
      }
    } catch (error) {
      console.error("Error al obtener las cartas:", error);
    }
  };

  return (
    <div>
      <h2>Generador de Deck Automático</h2>
      <input
        type="text"
        value={archetype}
        onChange={(e) => setArchetype(e.target.value)}
        placeholder="Escribe el arquetipo..."
      />
      
      {/* Dropdown para elegir tamaño del deck */}
      <select value={deckSize} onChange={(e) => setDeckSize(Number(e.target.value))}>
        <option value={20}>20 Cartas</option>
        <option value={30}>30 Cartas</option>
        <option value={40}>40 Cartas</option>
        <option value={50}>50 Cartas</option>
      </select>

      <button onClick={fetchArchetypeCards}>Generar Deck</button>

      {deck.length > 0 && (
        <div>
          <h3>Deck Generado ({deckSize} cartas)</h3>
          <ul>
            {deck.map((card) => (
              <li key={card.id}>
                <img src={card.card_images[0].image_url} alt={card.name} width="100" />
                <p>{card.name}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AutoDeckBuilder;
