import React from "react";

function DeckBuilder({ selectedCards, setSelectedCards }) {
  const removeFromDeck = (index) => {
    const updatedDeck = [...selectedCards];
    updatedDeck.splice(index, 1);
    setSelectedCards(updatedDeck);
  };

  const moveCard = (index, direction) => {
    const updatedDeck = [...selectedCards];
    const newIndex = index + direction;

    if (newIndex >= 0 && newIndex < updatedDeck.length) {
      [updatedDeck[index], updatedDeck[newIndex]] = [updatedDeck[newIndex], updatedDeck[index]];
      setSelectedCards(updatedDeck);
    }
  };

  return (
    <div>
      <h2>Tu Deck</h2>
      {selectedCards.length === 0 ? (
        <p>No has agregado cartas al deck.</p>
      ) : (
        <ul>
          {selectedCards.map((card, index) => (
            <li key={index}>
              <img src={card.card_images[0].image_url} alt={card.name} width="100" />
              <p>{card.name}</p>
              <button onClick={() => removeFromDeck(index)}>Eliminar</button>
              <button onClick={() => moveCard(index, -1)}>⬆️</button>
              <button onClick={() => moveCard(index, 1)}>⬇️</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default DeckBuilder;
