import React, { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig";
import { collection, getDocs, query, where } from "firebase/firestore";

function CardList({ cards = [], setCards = () => {} }) {
  const [savedCards, setSavedCards] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Verificar si el usuario estÃ¡ autenticado
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      if (user) {
        loadUserDeck(user.uid);
      }
    });

    return () => unsubscribe();
  }, []);



  // ðŸ”¹ Permitir seleccionar cartas guardadas para un nuevo deck
  const handleSelectCard = (card) => {
    console.log("Carta seleccionada:", card); // âœ… Verifica si se ejecuta
    if (typeof setCards !== "function") {
      console.error("setCards no estÃ¡ definido correctamente.");
      return;
    }
    setCards((prevCards) => [...prevCards, card]); 
  };

  return (
    <div>
      <h2>Cartas Seleccionadas</h2>
      {loading ? <p>Cargando cartas guardadas...</p> : null}

      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {/* Mostrar cartas seleccionadas en la sesiÃ³n actual */}
        {cards.map((card, index) => (
          <div key={index} style={{ margin: "10px", textAlign: "center" }}>
            <img src={card.card_images[0].image_url} alt={card.name} width="100" />
            <p>{card.name}</p>
          </div>
        ))}

        {/* Mostrar cartas recuperadas de Firestore con opciÃ³n de seleccionarlas */}
        {savedCards.map((card, index) => (
  <div key={`saved-${index}`} style={{ margin: "10px", textAlign: "center" }}>
    <img src={card.card_images[0].image_url} alt={card.name} width="100" />
    <p>{card.name}</p>
    <button onClick={() => handleSelectCard(card)}>Seleccionar</button>
  </div>
))}
      </div>
    </div>
  );
}

export default CardList;
