import React, { useEffect } from "react";

function CardList({ cards, setCards }) {
  // Cargar la lista de cartas desde localStorage al iniciar
  useEffect(() => {
    const savedCards = JSON.parse(localStorage.getItem("selectedCards"));
    if (savedCards) {
      setCards(savedCards);
    }
  }, []);

  // Guardar en localStorage cuando cambia la lista de cartas
  useEffect(() => {
    localStorage.setItem("selectedCards", JSON.stringify(cards));
  }, [cards]);

  // Función para eliminar una carta específica
  const removeCard = (instanceId) => {
    const updatedCards = cards.filter((card) => card.instanceId !== instanceId);
    setCards(updatedCards);
  };

  return (
    <div>
      <h2>Cartas Seleccionadas</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {cards.length > 0 ? (
          cards.map((card) => (
            <div
              key={card.instanceId}
              style={{ margin: "10px", textAlign: "center" }}
            >
              <img
                src={card.card_images[0].image_url}
                alt={card.name}
                width="100"
              />
              <p>{card.name}</p>
              <button onClick={() => removeCard(card.instanceId)}>Eliminar</button>
            </div>
          ))
        ) : (
          <p>No hay cartas seleccionadas.</p>
        )}
      </div>
    </div>
  );
}

export default CardList;
