import React, { useState } from 'react';

function CardUploader() {
  const [cards, setCards] = useState([]);
  const [error, setError] = useState(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        try {
          const cardData = JSON.parse(reader.result);
          setCards(cardData);
          setError(null);
        } catch (err) {
          setError('Error al leer el archivo JSON. Asegúrate de que el archivo esté en el formato correcto.');
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <div>
      <h2>Sube tu colección de cartas</h2>
      {/* Formulario para subir el archivo */}
      <input type="file" accept=".json" onChange={handleFileUpload} />
      
      {/* Mensaje de error si ocurre un problema */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      {/* Tabla para mostrar las cartas subidas */}
      <div>
        <h3>Colección de cartas:</h3>
        {cards.length > 0 ? (
          <table border="1" cellPadding="10">
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Tipo</th>
                <th>Ataque</th>
                <th>Defensa</th>
                <th>Descripción</th>
                <th>Efecto</th>
              </tr>
            </thead>
            <tbody>
              {cards.map((card, index) => (
                <tr key={index}>
                  <td>{card.name}</td>
                  <td>{card.type}</td>
                  <td>{card.attack}</td>
                  <td>{card.defense}</td>
                  <td>{card.description}</td>
                  <td>{card.effect ? 'Sí' : 'No'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No se han subido cartas aún.</p>
        )}
      </div>
    </div>
  );
}

export default CardUploader;
