import React, { useState } from "react";

function CardSearch({ addToDeck }) {
  const [query, setQuery] = useState("");
  const [card, setCard] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchCardInfo = async () => {
    setLoading(true);
    setError("");
    setCard(null);

    try {
      const response = await fetch(
        `https://db.ygoprodeck.com/api/v7/cardinfo.php?name=${encodeURIComponent(query)}`
      );
      const data = await response.json();

      if (data.data) {
        setCard(data.data[0]); // Tomamos la primera coincidencia
      } else {
        setError("Carta no encontrada. Inténtalo con otro nombre.");
      }
    } catch (err) {
      setError("Error al buscar la carta.");
    }

    setLoading(false);
  };

  return (
    <div>
      <h2>Buscador de Cartas</h2>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Escribe el nombre de la carta..."
      />
      <button onClick={fetchCardInfo}>Buscar</button>

      {loading && <p>Buscando carta...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {card && (
        <div>
          <h3>{card.name}</h3>
          <img src={card.card_images[0].image_url} alt={card.name} width="150" />
          <p><strong>Tipo:</strong> {card.type}</p>
          <p><strong>Descripción:</strong> {card.desc}</p>
          <button onClick={() => addToDeck(card)}>Agregar al mazo</button>
        </div>
      )}
    </div>
  );
}

export default CardSearch;
